import React, { useState } from 'react';
import PatentInputForm from './components/PatentInputForm';
import ResultDisplay from './components/ResultDisplay';

function App() {
    const [result, setResult] = useState(null);

    const handleCheckInfringement = ({ patentId, companyName }) => {
        fetch('http://localhost:3000/check-infringement', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ patentId, companyName })
        })
        .then(response => response.json())
        .then(data => setResult(data))
        .catch(error => console.error('Error checking infringement:', error));
    };

    return (
        <div className="App" style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
            <h1>Patent Infringement Checker</h1>
            <PatentInputForm onSubmit={handleCheckInfringement} />
            <ResultDisplay result={result} />
        </div>
    );
}

export default App;
