import React from 'react';

function ResultDisplay({ result }) {
    if (!result) return null;

    return (
        <div className="result-display">
            <h2>Check Results</h2>
            {result.message === 'Data matches the record.' ? (
                <div>
                    <h3>Patent Details:</h3>
                    <p><strong>Publication Number:</strong> {result.patent.publication_number}</p>
                    <p><strong>Title:</strong> {result.patent.title}</p>
                    <h3>Company Details:</h3>
                    <p><strong>Company Name:</strong> {result.company.name}</p>
                    <p><strong>Products:</strong></p>
                    <ul>
                        {result.company.products.map((product, index) => (
                            <li key={index}>{product.name}: {product.description}</li>
                        ))}
                    </ul>
                </div>
            ) : (
                <p>{result.message}</p>
            )}
        </div>
    );
}

export default ResultDisplay;
