import React, { useEffect, useState } from 'react';

// 假设你可以使用 import 语句加载 JSON 数据
import companyProducts from '../data/company_products.json';
import patents from '../data/patents.json';

function ResultDisplay({ result }) {
    const [isMatch, setIsMatch] = useState(false);

    useEffect(() => {
        if (result) {
            // 检查是否有匹配的专利和公司产品
            const patentMatch = patents.find(patent => patent.id === result.patentId);
            const companyMatch = companyProducts.find(company => company.name === result.companyName);

            if (patentMatch && companyMatch) {
                setIsMatch(true);
            } else {
                setIsMatch(false);
            }
        }
    }, [result]);

    if (!result) return null;

    return (
        <div className="result-display">
            <h2>Check Results</h2>
            {isMatch ? (
                <p>Data matches the record.</p>
            ) : (
                <p>Data does not match the record.</p>
            )}
            {result.infringingProducts && result.infringingProducts.length > 0 ? (
                <div>
                    <h3>Infringing Products:</h3>
                    {result.infringingProducts.map((product, index) => (
                        <div key={index}>
                            <h4>{product.name}</h4>
                            <p>{product.description}</p>
                            <p><strong>Infringement Details:</strong> {product.infringementDetails}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p>{result.message}</p>
            )}
        </div>
    );
}

export default ResultDisplay;
