import React, { useState } from 'react';
import PatentInputForm from './components/PatentInputForm';
import ResultDisplay from './components/ResultDisplay';

function App() {
    const [result, setResult] = useState(null);

    const handleCheckInfringement = async ({ patentId, companyName }) => {
        try {
            const response = await fetch('http://localhost:5001/check_infringement', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ patentId, companyName })
            });
            const data = await response.json();
            setResult(data.results);
        } catch (error) {
            console.error("Error fetching infringement results:", error);
        }
    };

    return (
        <div className="App" style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
            <h1>專利侵權檢查器</h1>
            <PatentInputForm onSubmit={handleCheckInfringement} />
            <ResultDisplay result={result} />
        </div>
    );
}

export default App;
