import React, { useState, useEffect } from 'react';
import PatentInputForm from './components/PatentInputForm';
import ResultDisplay from './components/ResultDisplay';

function App() {
    const [result, setResult] = useState(null);
    const [patents, setPatents] = useState([]);
    const [companies, setCompanies] = useState([]);

    useEffect(() => {
        // Fetch patents data
        fetch('/data/patents.json')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setPatents(data))
            .catch(error => console.error('Error fetching patents:', error));

        // Fetch company products data
        fetch('/data/company_products.json')
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => setCompanies(data.companies))
            .catch(error => console.error('Error fetching companies:', error));
    }, []);

    const handleCheckInfringement = ({ patentId, companyName }) => {
        const patent = patents.find(p => p.publication_number === patentId);
        const company = companies.find(c => c.name === companyName);

        if (patent && company) {
            // 假設每個產品都有一個可能的侵權信息字段
            const infringingProducts = company.products
                .filter(product => product.potentialInfringement && product.potentialInfringement.includes(patentId))
                .slice(0, 2) // 只取前兩個
                .map(product => ({
                    name: product.name,
                    description: product.description,
                    infringementDetails: product.potentialInfringementDetails
                }));

            setResult({
                patent: patent,
                company: company,
                infringingProducts: infringingProducts,
                message: infringingProducts.length > 0 ? 'Infringing products found.' : 'No infringing products found.'
            });
        } else {
            setResult({
                message: 'Patent or company not found in the database.'
            });
        }
    };

    return (
        <div className="App" style={{ padding: '20px', maxWidth: '800px', margin: '0 auto' }}>
            <h1>Patent Infringement Checker</h1>
            <PatentInputForm onSubmit={handleCheckInfringement} />
            <ResultDisplay result={result} />
        </div>
    );
}

export default App;
